// Get vocabulary by category
export function getVocabularyByCategory(category: string): VocabEntry[] {
  return vocabularyData.filter(v => v.category === category);
}

// Get vocabulary by difficulty
export function getVocabularyByDifficulty(difficulty: 1 | 2 | 3): VocabEntry[] {
  return vocabularyData.filter(v => v.difficulty === difficulty);
}

// Get all categories
export function getAllCategories(): string[] {
  return [...new Set(vocabularyData.map(v => v.category))];
}

// Search vocabulary
export function searchVocabulary(query: string): VocabEntry[] {
  const q = query.toLowerCase();
  return vocabularyData.filter(v =>
    v.english.toLowerCase().includes(q) ||
    v.japanese.includes(q)
  );
}

// Get random words for study
export function getRandomWords(count: number, difficulty?: 1 | 2 | 3): VocabEntry[] {
  let pool = difficulty ? vocabularyData.filter(v => v.difficulty === difficulty) : vocabularyData;
  const shuffled = [...pool].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count);
}

// Total vocabulary count
export const TOTAL_VOCABULARY_COUNT = vocabularyData.length;
